export default function FarmerMarketplace() {
  const { addToCart } = useCartStore()
  const navigate = useNavigate()
  const [activeCategory, setActiveCategory] = useState<string>("fruits")

  // Scroll to active category on mount and when category changes
  useEffect(() => {
    const element = document.getElementById(`${activeCategory}-section`)
    if (element) {
      element.scrollIntoView({ behavior: "smooth", block: "start" })
    }
  }, [activeCategory])

  const handleAddToCart = (product: any) => {
    // Convert price string to number (remove "₹" and "per kg/dozen/box" etc)
    const priceNumber = Number.parseInt(product.price.replace(/[^0-9]/g, ""))

    const cartItem = {
      _id: product.id.toString(),
      name: product.name,
      price: priceNumber,
      discount: product.discount,
      description: `${product.name} from ${product.location}`,
      image: product.image,
      quantity: 1,
    }

    addToCart(cartItem)
    navigate("/cart")
  }

  const scrollSection = (sectionId: string, direction: "left" | "right") => {
    const section = document.getElementById(sectionId)
    if (section) {
      const scrollAmount = direction === "left" ? -400 : 400
      section.scrollBy({ left: scrollAmount, behavior: "smooth" })
    }
  }

  // Calculate discounted price
  const calculateDiscountedPrice = (price: string, discount: string) => {
    const priceValue = Number.parseInt(price.replace(/[^0-9]/g, ""))
    const discountValue = Number.parseInt(discount.replace(/[^0-9]/g, "")) || 0
    const discountedPrice = priceValue - (priceValue * discountValue / 100)
    
    // Extract the unit (per kg, per dozen, etc.)
    const unit = price.match(/per\s+[a-zA-Z]+/)?.[0] || ""
    
    return `₹${discountedPrice} ${unit}`
  }

  // Product Card Component
  const ProductCard = ({ product }: { product: (typeof products.fruits)[0] }) => (
    <Card className="w-[280px] sm:w-[300px] flex-shrink-0 overflow-hidden transition-transform hover:scale-[1.02]">
      <div className="relative aspect-square">
        <img
          src={product.image}
          alt={product.name}
          className="object-cover w-full h-full"
        />
        {product.discount && (
          <Badge className="absolute top-2 right-2 bg-red-500">
            {product.discount} OFF
          </Badge>
        )}
      </div>
      <CardContent className="p-3 sm:p-4">
        <h3 className="mb-2 text-base sm:text-lg font-bold text-gray-800 line-clamp-1">
          {product.name}
        </h3>
        <div className="flex items-center justify-between mb-2 sm:mb-3">
          <div className="flex flex-col">
            {product.discount && (
              <span className="text-xs sm:text-sm text-gray-500 line-through">
                {product.price}
              </span>
            )}
            <span className="text-base sm:text-lg font-semibold text-green-600">
              {calculateDiscountedPrice(product.price, product.discount)}
            </span>
          </div>
          <div className="flex items-center bg-green-50 px-1.5 sm:px-2 py-1 rounded-full">
            <Star className="w-3 h-3 sm:w-4 sm:h-4 mr-1 text-yellow-500 fill-current" />
            <span className="text-xs sm:text-sm text-gray-600">{product.rating}</span>
          </div>
        </div>
        <div className="flex items-center gap-1 mb-3 sm:mb-4 text-xs sm:text-sm text-gray-500">
          <MapPin size={14} className="text-green-500 flex-shrink-0" />
          <span className="truncate">{product.location}</span>
        </div>
        <Button
          className="w-full text-white transition-all duration-300 rounded-full bg-green-500 hover:bg-green-600 text-sm sm:text-base"
          onClick={() => handleAddToCart(product)}
        >
          <ShoppingCart className="w-3 h-3 sm:w-4 sm:h-4 mr-2" />
          Add to Cart
        </Button>
      </CardContent>
    </Card>
  )

  // Category Navigation
  const CategoryNav = () => (
    <div className="flex flex-wrap justify-center gap-2 sm:gap-4 mb-6 sm:mb-8 px-2">
      <Button
        variant={activeCategory === "fruits" ? "default" : "outline"}
        className={`rounded-full px-4 sm:px-6 py-1 sm:py-2 text-sm sm:text-base ${
          activeCategory === "fruits" 
            ? "bg-green-500 hover:bg-green-600 text-white" 
            : "border-green-200 text-green-600 hover:bg-green-50"
        }`}
        onClick={() => setActiveCategory("fruits")}
      >
        <Apple className="w-3 h-3 sm:w-4 sm:h-4 mr-1 sm:mr-2" />
        Fruits
      </Button>
      <Button
        variant={activeCategory === "vegetables" ? "default" : "outline"}
        className={`rounded-full px-6 ${
          activeCategory === "vegetables" 
            ? "bg-green-500 hover:bg-green-600 text-white" 
            : "border-green-200 text-green-600 hover:bg-green-50"
        }`}
        onClick={() => setActiveCategory("vegetables")}
      >
        <Carrot className="w-4 h-4 mr-2" />
        Vegetables
      </Button>
      <Button
        variant={activeCategory === "dairy" ? "default" : "outline"}
        className={`rounded-full px-6 ${
          activeCategory === "dairy" 
            ? "bg-green-500 hover:bg-green-600 text-white" 
            : "border-green-200 text-green-600 hover:bg-green-50"
        }`}
        onClick={() => setActiveCategory("dairy")}
      >
        <Milk className="w-4 h-4 mr-2" />
        Dairy
      </Button>
    </div>
  )

  // Scrollable Section Component
  const ScrollableSection = ({ title, sectionId, items, icon }: {
    title: string;
    sectionId: string;
    items: typeof products.fruits;
    icon: React.ReactNode;
  }) => (
    <div className="mb-8 sm:mb-16" id={sectionId}>
      <div className="flex items-center justify-between mb-4 sm:mb-6 px-2">
        <div className="flex items-center gap-2 sm:gap-3">
          <div className="p-1.5 sm:p-2 bg-green-100 rounded-full text-green-600">
            {icon}
          </div>
          <h2 className="text-xl sm:text-2xl font-bold text-gray-800">{title}</h2>
        </div>
        <div className="flex gap-1 sm:gap-2">
          <Button
            variant="outline"
            size="icon"
            className="w-8 h-8 sm:w-10 sm:h-10 rounded-full border-green-200 text-green-600 hover:bg-green-50"
            onClick={() => scrollSection(sectionId + "-items", "left")}
          >
            <ChevronLeft className="w-4 h-4 sm:w-5 sm:h-5" />
          </Button>
          <Button
            variant="outline"
            size="icon"
            className="w-8 h-8 sm:w-10 sm:h-10 rounded-full border-green-200 text-green-600 hover:bg-green-50"
            onClick={() => scrollSection(sectionId + "-items", "right")}
          >
            <ChevronRight className="w-4 h-4 sm:w-5 sm:h-5" />
          </Button>
        </div>
      </div>
      <div
        id={sectionId + "-items"}
        className="flex gap-3 sm:gap-6 pb-4 overflow-x-auto scrollbar-hide snap-x"
        style={{ 
          scrollbarWidth: "none", 
          msOverflowStyle: "none",
          WebkitOverflowScrolling: "touch",
          paddingBottom: "20px",
        }}
      >
        {items.map((product) => (
          <div key={product.id} className="snap-start">
            <ProductCard product={product} />
          </div>
        ))}
      </div>
    </div>
  )
  return (
    <div>
      {/* Hero Section */}
      <div 
        
        style={{
          backgroundImage: "linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url('/images/farm-background.jpg')",
          backgroundSize: "cover",
        }}
      >
      </div>

      {/* Marketplace Section */}
      <div id="marketplace" className="px-4 py-12 md:px-8 max-w-7xl">
        <div className="mb-10 text-center">
          <h2 className="mb-4 text-3xl font-bold text-gray-800">Our Fresh Products</h2>
          <p className="max-w-2xl mx-auto text-gray-600">
            Browse our selection of fresh, locally-sourced products. All items are harvested within 24 hours of delivery to ensure maximum freshness.
          </p>
        </div>
        

        <CategoryNav />

        <ScrollableSection 
          title="Fresh Fruits" 
          sectionId="fruits-section" 
          items={products.fruits}
          icon={<Apple className="w-5 h-5" />}
        />
        <ScrollableSection 
          title="Farm Vegetables" 
          sectionId="vegetables-section" 
          items={products.vegetables}
          icon={<Carrot className="w-5 h-5" />}
        />
        <ScrollableSection 
          title="Dairy Products" 
          sectionId="dairy-section" 
          items={products.dairy}
          icon={<Milk className="w-5 h-5" />}
        />
       
      </div>

      {/* Features Section */}
      <div className="py-16 bg-green-50">
        <div className="px-4 mx-auto max-w-7xl">
          <h2 className="mb-12 text-3xl font-bold text-center text-gray-800">Why Choose Our Marketplace?</h2>
          
          <div className="grid gap-8 md:grid-cols-3">
            <div className="p-6 text-center bg-white rounded-xl shadow-sm">
              <div className="flex items-center justify-center w-16 h-16 mx-auto mb-4 bg-green-100 rounded-full">
                <svg xmlns="http://www.w3.org/2000/svg" className="w-8 h-8 text-green-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
              <h3 className="mb-3 text-xl font-semibold">Farm Fresh</h3>
              <p className="text-gray-600">All products are harvested within 24 hours of delivery to ensure maximum freshness and nutrition.</p>
            </div>
            
            <div className="p-6 text-center bg-white rounded-xl shadow-sm">
              <div className="flex items-center justify-center w-16 h-16 mx-auto mb-4 bg-green-100 rounded-full">
                <svg xmlns="http://www.w3.org/2000/svg" className="w-8 h-8 text-green-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 6l3 1m0 0l-3 9a5.002 5.002 0 006.001 0M6 7l3 9M6 7l6-2m6 2l3-1m-3 1l-3 9a5.002 5.002 0 006.001 0M18 7l3 9m-3-9l-6-2m0-2v2m0 16V5m0 16H9m3 0h3" />
                </svg>
              </div>
              <h3 className="mb-3 text-xl font-semibold">Fair Prices</h3>
              <p className="text-gray-600">We ensure farmers get fair compensation while keeping prices reasonable for consumers.</p>
            </div>
            
            <div className="p-6 text-center bg-white rounded-xl shadow-sm">
              <div className="flex items-center justify-center w-16 h-16 mx-auto mb-4 bg-green-100 rounded-full">
                <svg xmlns="http://www.w3.org/2000/svg" className="w-8 h-8 text-green-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
                </svg>
              </div>
              <h3 className="mb-3 text-xl font-semibold">Fast Delivery</h3>
              <p className="text-gray-600">Same-day delivery available for orders placed before noon, ensuring you get the freshest produce.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
